def write_dataset(dataset, filename, runId):
    output_path = 'abfss://ffs@abdatalake.dfs.core.windows.net/Transformed Datasets/' + filename + str(runId)
    dataset.coalesce(1).write.format('csv').mode('overwrite').option('header',True).save(output_path)
